import App from '/Users/siddharthkapoor/Projects/loki/demo/src/app.js';
import io from 'socket.io/socket.io.js';

window.addEventListener('load', function() {
    var app = new App({selector: 'todo-app', toplevel: true});
    window['todo-app'] = app;

    var socket = io();
 
    socket.on('refresh', () => window.location.reload());
});